css:
.table tr td{
    min-width:100px;
}
.text-bold {
  font-weight: bold;
}
.br {
    border-bottom: 1px dashed #000;
}  
.letter-head-header{
    display:flex;
    justify-content: space-between;
    align-items: center; 
    margin-bottom:25px;
}  
.image_logo{
    width: 165px;
}
.image_logo img{
    width: 100%;
}   
.print-format .row:not(.section-break){
    margin-top:6px !important;
} 
.page_title{
    font-size: 24px;
    font-weight: 600;
    color: #000;  
    margin: 0;
}
.print-format .table-bordered td, .print-format .table-bordered th{
    border:1px solid #000;
    color:#000;
}
.print-format .table-bordered.second_table th{
    background:#008dff4f; 
}
.table>thead:first-child>tr:first-child>th{
    border-top:1px solid #000 !important;
}
.value {
    border-bottom: 1px solid #000;
}
.sub_title{
    font-size: 18px;
    margin-bottom:0px;
    font-weight: 600;
} 
.header{
    background:#0060af;
    padding:5px; 
}
.header .sub_title{
    font-size: 16px;
    margin-bottom:0px;
    font-weight: 600;
}  
.print-format{
    padding:20px 10px;   
}
.content{
    /*border:2px solid #000;*/
    padding-bottom:25px;
}
.content .header{
    border:1px solid #000;
    border-bottom: none;
}
.page_footer{
    position: fixed;
    bottom: 0;
    width: 100%;
} 

.page_footer .fline{
    border-top:1px solid #0060ad;
    margin-top: 10px;
    color:#0060ad;
}
.page_footer .sign{
		width:calc(100% - 20px);
    margin-bottom: 30px;
}
@media print { 
    .image_logo {
    width: 120px;
} 
    .print-format .table-bordered{
            border: 1px solid #000;
    }
    .table>:not(:last-child)>:last-child>* {
        border-bottom-color: #000;
    }
    .print-format .table-bordered td, .print-format .table-bordered th {
        border: 1px solid #000 !important;
        color:#000;
        font-size: 14px;
        /*padding:0px !important;*/
    } 
    .print-format .table-bordered td{
        font-size: 14px !important;
   /* padding:0px !important;*/
